import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Post from "./Post";
import NewPost from "./NewPost";

const Feed = () => {
  // Un-comment the lines below to complete your solution
  // ====================

  const [data, setStateA] = useState();

  const getPostsData = () => {
    axios
      .get("http://localhost:3002/posts") //THIS IS YOUR URL OF YOUR API
      .then((data) => setStateA(data.data)) //PROMISE API, THAT MEANS WHEN YOU GET THE DATA WHAT DO I DO WITH IT
      .catch((err) => console.log(err));  //ERROR CATCHING IN CASE WE RECIEVE AN ERROR
  };
  useEffect(() => {
    getPostsData();
  }, [data])
  alert(data);

  // ====================

  return (
    <div style={{ maxWidth: '600px', marginLeft: 'auto', marginRight: 'auto'}}>
      <h1>HELLO</h1>
      {
        data && data.map(d =>
          <Post title={d[0].title} body={d[0].body} comments={d[0].comments} id={d[0].id} key={d[0].id} />
        )

      }

      {/* <NewPost __________ /> */}
    </div>
  )

}


export default Feed;
